// Short product name
const shorten = (name) => {
    const productName = name.split(" ");
    return `${productName[0]} ${productName[1]}`.toLowerCase();
}

const filterProductsPrice = (products, minPrice, maxPrice) => {
    if (minPrice && maxPrice) {
        const newProducts = products.filter((product) => {
            return product.price >= minPrice && product.price <= maxPrice
        })

        return newProducts;
    } else {
        return products;
    }
}

/*Search products*/
const searchProducts = (products, value) => {
    if (value.trim() !== "") {
        const filteredProducts = products.filter((product) => {
            return shorten(product.title).indexOf(value) !== -1;
        });
    
        return filteredProducts;
    }
}

export { shorten, filterProductsPrice, searchProducts };
