// Short product name
const shorten = (name) => {
    const productName = name.split(" ");
    return `${productName[0]} ${productName[1]}`.toLowerCase();
}

// Product is in cart?
const isInCart = (products, product) => {
    products.find(item => {
        return item.id === product.id
    })
}

export { shorten };
