import { useContext, useEffect, useState } from "react";
import { ProductContext } from "../../contexts/ProductContextProvider";
import Header from "../../components/Header/Header";
import styles from "./Products.module.css";
import Product from "../../components/Product/Product";
import { Link } from "react-router-dom";
import { filterProductsPrice, searchProducts } from "../../helpers/functions";

const Products = () => {

    const productsContext = useContext(ProductContext);

    const [sortingMode, setSortingMode] = useState("default");
    const [sortedProducts, setSortedProducts] = useState([]);
    const [searchText, setSearchText] = useState("");
    const [minPrice, setMinPrice] = useState(0);
    const [maxPrice, setMaxPrice] = useState(0);

    const sortingProducts = (type) => {
        if (type === "inexpensive") {
            const newProducts = sortedProducts.sort(
                (p1, p2) => (p1.price > p2.price) ? 1 : (p1.price < p2.price) ? -1 : 0);

            setSortedProducts(newProducts);
            setSortingMode("inexpensive");

        } else if (type === "expensive") {
            const newProducts = sortedProducts.sort(
                (p1, p2) => (p1.price < p2.price) ? 1 : (p1.price > p2.price) ? -1 : 0);

            setSortedProducts(newProducts);
            setSortingMode("expensive");

        } else if (type === "default") {
            setSortedProducts(productsContext);
            setSortingMode("default");
        }
    }

    const filterHandler = (products, minPrice, maxPrice) => {
        setSortedProducts(filterProductsPrice(products, minPrice, maxPrice));
    }

    const searchHandler = (text) => {
        searchProducts(sortedProducts, text);
    }

    useEffect(() => {
        const fetchPosts = fetch("https://fakestoreapi.com/products");
        const data = fetchPosts.then(res => res.json()).then(res => {
            setSortedProducts(res);
        })
    }, [])

    return (
        <>
            <Header />

            <div className="container mt-5">
                <div className="row justify-content-center">
                    <div className="col-3 p-2 mt-3">

                        <div className="p-4 border rounded-4">
                            {/*Title*/}
                            <h5 className={styles.title}>
                                <span>
                                    فیلتر کردن
                                </span>
                                <div>

                                </div>
                            </h5>

                            {/*Price filter*/}
                            <label htmlFor="minPrice" className={`${styles.label} mt-3 mb-1`}>حداقل قیمت</label>
                            <input type="text" placeholder="حداقل قیمت :" className="form-control form-control-sm"
                                onChange={event => setMinPrice(event.target.value)} value={minPrice} id="minPrice" />

                            <label htmlFor="maxPrice" className={`${styles.label} mt-3 mb-1`}>حداکثر قیمت</label>
                            <input type="text" placeholder=" حداکثر قیمت :" className="form-control form-control-sm"
                                onChange={event => setMaxPrice(event.target.value)} value={maxPrice} id="maxPrice" />

                            <button className="btn btn-outline-danger w-100 mt-2"
                                onClick={() => { filterHandler(sortedProducts, minPrice, maxPrice) }}>
                                اعمال فیلتر
                            </button>

                            <hr className="mt-4" />

                            {/*Search in products*/}
                            <h5 className={styles.title}>
                                <span>جستوجو</span>
                                <div></div>
                            </h5>

                            <input type="text" placeholder="نام محصول ..." className="form-control form-control-sm mt-3"
                                value={searchText}
                                onChange={(event) => { setSearchText(event.target.value) }} />

                            <button className="btn btn-outline-danger w-100 mt-2"
                            onClick={searchHandler}>
                                جستوجو
                            </button>
                        </div>

                        {/*Ad*/}
                        <img src="https://img.soft98.ir/1/zula.gif" alt="AD" className="img-fluid rounded-3 d-block mx-auto mt-3" />
                    </div>

                    {/*Product list*/}
                    <div className="col-9 p-3">

                        <div className="d-flex justify-content-between align-items-center mt-3">

                            {/*breadcrumb*/}
                            <nav>
                                <ol className="breadcrumb">
                                    <li className="breadcrumb-item">
                                        <Link className="text-dark text-decoration-none" to="/">صفحه اصلی</Link>
                                    </li>
                                    <li className="breadcrumb-item active">فروشگاه</li>
                                </ol>
                            </nav>

                            <div className={`${styles.sort} d-flex`}>
                                مرتب سازی بر اساس:

                                <ul className="d-flex">
                                    <li>
                                        <Link to={"/products"} onClick={() => {
                                            sortingProducts("inexpensive");
                                        }}
                                            className={sortingMode === "inexpensive" ? styles.active : ""}>
                                            ارزان ترین
                                        </Link>
                                    </li>
                                    <li className="mx-2">
                                        <Link to={"/products"} onClick={() => {
                                            sortingProducts("expensive");
                                        }}
                                            className={sortingMode === "expensive" ? styles.active : ""}>
                                            گران ترین
                                        </Link>
                                    </li>
                                    <li>
                                        <Link to={"/products"} onClick={() => {
                                            sortingProducts("default");
                                        }}
                                            className={sortingMode === "default" ? styles.active : ""}>
                                            جدید ترین
                                        </Link>
                                    </li>
                                </ul>
                            </div>

                        </div>

                        <div className="row row-cols-3 h-100">
                            {
                                sortedProducts.map(item => <Product key={item.id} product={item} />)
                            }
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}

export default Products;
