import { useContext, useEffect, useState } from "react";
import { ProductContext } from "../../contexts/ProductContextProvider";
import { shorten, categorizeProducts } from "../../helpers/functions";
import Header from "../../components/Header/Header";
import styles from "./Products.module.css";
import { useLocation } from "react-router-dom";
import queryString from "query-string";
import Product from "../../components/Product/Product";
import { Link } from "react-router-dom";

const Products = () => {

    const products = useContext(ProductContext);

    const [productsState, setProducts] = useState([...products]);

    const location = useLocation();

    const sortingMethod = queryString.parseUrl(location.search);

    if (sortingMethod.query.sort_by === "inexpensive") {
        const sortedProducts = products.sort(
            (p1, p2) => (p1.price > p2.price) ? 1 : (p1.price < p2.price) ? -1 : 0);
        setProducts(sortedProducts);
    }

    return (
        <>
            <Header />

            <div className="container mt-5">
                <div className="row justify-content-center">
                    <div className="col-3 p-2 mt-3">

                        <div className="p-4 border rounded-4">
                            {/*Title*/}
                            <h5 className={styles.title}>
                                <span>
                                    فیلتر کردن
                                </span>
                                <div>

                                </div>
                            </h5>

                            {/*Price filter*/}
                            <input type="text" placeholder="حداقل قیمت :" className="form-control form-control-sm mt-3" />
                            <input type="text" placeholder=" حداکثر قیمت :" className="form-control form-control-sm mt-3" />

                            <button className="btn btn-outline-danger w-100 mt-2">
                                اعمال فیلتر
                            </button>

                            <hr className="mt-4" />

                            {/*Search in products*/}
                            <h5 className={styles.title}>
                                <span>جستوجو</span>
                                <div></div>
                            </h5>

                            <input type="text" placeholder="نام محصول ..." className="form-control form-control-sm mt-3" />

                            <button className="btn btn-outline-danger w-100 mt-2">
                                جستوجو
                            </button>
                        </div>

                        {/*Ad*/}
                        <img src="https://img.soft98.ir/1/zula.gif" alt="AD" className="img-fluid rounded-3 d-block mx-auto mt-3" />
                    </div>

                    {/*Product list*/}
                    <div className="col-9 p-3">

                        <div className="d-flex justify-content-between align-items-center">

                            {/*breadcrumb*/}
                            <nav>
                                <ol className="breadcrumb">
                                    <li className="breadcrumb-item">
                                        <Link className="text-dark text-decoration-none" to="/">صفحه اصلی</Link>
                                    </li>
                                    <li className="breadcrumb-item active">فروشگاه</li>
                                </ol>
                            </nav>

                            <div className={`${styles.sort} d-flex`}>
                                مرتب سازی بر اساس:

                                <ul className="d-flex">
                                    <li>
                                        <Link to={"/products?sort_by=inexpensive"}>
                                            ارزان ترین
                                        </Link>
                                    </li>
                                    <li className="mx-2">
                                        <Link>
                                            گران ترین
                                        </Link>
                                    </li>
                                    <li>
                                        <Link className={styles.active}>
                                            جدید ترین
                                        </Link>
                                    </li>
                                </ul>
                            </div>

                        </div>

                        <div className="row row-cols-3 h-100">
                            {
                                productsState === [] ? <h1>در حال بارگذاری ...</h1> :
                                    productsState.map(item => {
                                        return (
                                            <Product key={item.id} product={item} />
                                        )
                                    })
                            }
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}

export default Products;
