import { useContext, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { ProductContext } from "../../contexts/ProductContextProvider";
import Header from "../../components/Header/Header";
import styles from "./Products.module.css";
import Footer from "../../components/Footer/Footer";
import Product from "../../components/Product/Product";

const Products = () => {

    const productsContext = useContext(ProductContext);

    const [sortingMode, setSortingMode] = useState("default");
    const [sortedProducts, setSortedProducts] = useState([]);

    const sortingProducts = (type) => {
        if (type === "inexpensive") {
            const newProducts = sortedProducts.sort(
                (p1, p2) => (p1.price > p2.price) ? 1 : (p1.price < p2.price) ? -1 : 0);

            setSortedProducts(newProducts);
            setSortingMode("inexpensive");

        } else if (type === "expensive") {
            const newProducts = sortedProducts.sort(
                (p1, p2) => (p1.price < p2.price) ? 1 : (p1.price > p2.price) ? -1 : 0);

            setSortedProducts(newProducts);
            setSortingMode("expensive");

        } else if (type === "default") {
            setSortedProducts(productsContext);
            setSortingMode("default");
        }
    }

    useEffect(() => {
        const setProducts = () => {
            setSortedProducts([...productsContext]);
        }
        setProducts();
    }, [])

    return (
        <>
            <Header />

            <div className="container mt-5">
                <div className="row justify-content-center">
                    {/*Product list*/}
                    <div className="col-12 p-3">

                        <div className="d-flex justify-content-between align-items-center mt-3">

                            {/*breadcrumb*/}
                            <nav>
                                <ol className="breadcrumb">
                                    <li className="breadcrumb-item">
                                        <Link className="text-dark text-decoration-none" to="/">صفحه اصلی</Link>
                                    </li>
                                    <li className="breadcrumb-item active">فروشگاه</li>
                                </ol>
                            </nav>

                            <div className={`${styles.sort} d-flex`}>
                                مرتب سازی بر اساس:

                                <ul className="d-flex">
                                    <li>
                                        <Link to={"/products"} onClick={() => {
                                            sortingProducts("inexpensive");
                                        }}
                                            className={sortingMode === "inexpensive" ? styles.active : ""}>
                                            ارزان ترین
                                        </Link>
                                    </li>
                                    <li className="mx-2">
                                        <Link to={"/products"} onClick={() => {
                                            sortingProducts("expensive");
                                        }}
                                            className={sortingMode === "expensive" ? styles.active : ""}>
                                            گران ترین
                                        </Link>
                                    </li>
                                    <li>
                                        <Link to={"/products"} onClick={() => {
                                            sortingProducts("default");
                                        }}
                                            className={sortingMode === "default" ? styles.active : ""}>
                                            جدید ترین
                                        </Link>
                                    </li>
                                </ul>
                            </div>

                        </div>

                        <div className="row row-cols-4 h-100">
                            {
                                sortedProducts.map(item => <Product key={item.id} product={item} />)
                            }
                        </div>
                    </div>
                </div>
            </div>

            <Footer />
        </>
    )
}

export default Products;
