import { useEffect, useState } from "react";
import Header from "../../components/Header/Header";
import styles from "./Products.module.css";

const Products = () => {

    const [categorys, setCategorys] = useState([]);

    const getCategorys = () => {
        const fetchCategorys = fetch("http://localhost:4000/categorys");
        const data = fetchCategorys.then(res => {
            return res.json()
        }).then(res => setCategorys(res))
    }

    const filterCategorys = () => {
        const fetchCategorys = fetch("http://localhost:4000/categorys");
        const data = fetchCategorys.then(res => {
            return res.json()
        }).then(res => setCategorys(res))
    }

    useEffect(() => {
        getCategorys();
    }, [])

    return (
        <>
            <Header />

            <div className="container mt-5">
                <div className="row align-items-center justify-content-center">
                    <div className="col-3 p-3">
                        <div className="p-3 shadow rounded-3">
                            {/*Title*/}
                            <h5 className={styles.title}>
                                <span>
                                    فیلتر کردن
                                </span>
                                <div>

                                </div>
                            </h5>

                            {/*Price filter*/}
                            <input type="text" placeholder="حداقل قیمت :" className="form-control form-control-sm mt-3" />
                            <input type="text" placeholder=" حداکثر قیمت :" className="form-control form-control-sm mt-3" />

                            <hr className="my-4" />

                            {/*Product categorys*/}
                            <h6 className={styles.title}>
                                <span>دسته بندی محصولات</span>
                                <div></div>
                            </h6>

                            {
                                categorys.map(item => {
                                    return (
                                        <button className={styles.categoryButton} key={item.id}>
                                            {item.name}
                                        </button>
                                    )
                                })
                            }
                        </div>
                    </div>
                    <div className="col-9 p-3">

                    </div>
                </div>
            </div>
        </>
    )
}

export default Products;
